func getBathroomCode(instructions []string) string {
	keypad := [][]int{
		{1, 2, 3},
		{4, 5, 6},
		{7, 8, 9},
	}
	x, y := 1, 1 // Start at '5'
	var code string

	for _, instruction := range instructions {
		for _, move := range instruction {
			switch move {
			case 'U':
				if x > 0 {
					x--
				}
			case 'D':
				if x < 2 {
					x++
				}
			case 'L':
				if y > 0 {
					y--
				}
			case 'R':
				if y < 2 {
					y++
				}
			}
		}
		code += fmt.Sprint(keypad[x][y])
	}

	return code
}
