func elephant(input string) int {
	startingElves, _ := strconv.Atoi(input)
	root := &LLNode{
		elfNum:   1,
		presents: 1,
	}
	iter := root
	for i := 2; i <= startingElves; i++ {
		iter.next = &LLNode{
			elfNum:   i,
			presents: 1,
		}
		iter = iter.next
	}
