			avail, _ := strconv.Atoi(matches[2])
			nodes = append(nodes, node{used: used, avail: avail})
		}
	}
	return nodes
}

func countViablePairs(nodes []node) int {
	count := 0
	for i, a := range nodes {
		for j, b := range nodes {
			if i != j && a.used > 0 && a.used <= b.avail {
				count++
			}
		}
	}
	return count
