	}
	// move hole to adjacent to goal
	// swap goal and hole
	// repeat until goal at origin
	var sum int
	for goal != image.Pt(0, 0) {
		next := goal.Add(image.Pt(-1, 0))
		m, err := moves(nodes, goal, hole, next)
		if err != nil {
