	i := reg.FindAllStringIndex(text, -1)
	laststart := 0
	n := make([]string, len(i)+1)
	for i, element := range i {
		n[i] = text[laststart:element[0]]
		laststart = element[1]
	}
	n[len(i)] = text[laststart:]
	return n
