func buildMap(regex string) DoorMap {
	dm := make(DoorMap)
	var stack []Point
	cp := Point{0, 0}
	for _, c := range regex {
		if c == '(' {
			stack = append(stack, cp)
		} else if c == '|' {
			cp = stack[len(stack)-1]
		} else if c == ')' {
			cp = stack[len(stack)-1]
			stack = stack[:len(stack)-1]
		} else {
			np := move(cp, c)
			if dm[cp] == nil {
				dm[cp] = make(map[Point]bool)
			}
			dm[cp][np] = true
			cp = np
		}
	}
	return dm
}

func move(p Point, dir rune) Point {
	switch dir {
	case 'N':
		return Point{p.X, p.Y - 1}
	case 'S':
		return Point{p.X, p.Y + 1}
	case 'E':
		return Point{p.X + 1, p.Y}
	case 'W':
		return Point{p.X - 1, p.Y}
	}
	return p
}

