		if row < Side-1 {
			if grid[i+Side] {
				neighbours++
			}
		}
		if col > 0 {
			if grid[i-1] {
				neighbours++
			}
		}
		if col < Side-1 {
			if grid[i+1] {
				neighbours++
			}
		}

		if grid[i] && neighbours != 1 {
			newGrid[i] = false
			continue
		}

		if !grid[i] && (neighbours == 1 || neighbours == 2) {
			newGrid[i] = true
			continue
		}

		newGrid[i] = grid[i]
	}

	return newGrid
}

func biodiversity(grid [Square]bool) int {
	bio := 0
	for i := 0; i < Square; i++ {
		if grid[i] {
			bio += 1 << i
		}
	}
	return bio
}
