func readRules(scanner *bufio.Scanner) map[int]string {
	rules := make(map[int]string)
	for scanner.Scan() {
		line := scanner.Text()
		if line == "" {
			break
		}
		parts := strings.Split(line, ": ")
		rules[atoi(parts[0])] = strings.Replace(parts[1], "\"", "", -1)
	}
