func countMatches(scanner *bufio.Scanner, pattern string) int {
	count := 0
	re := regexp.MustCompile("^" + pattern + "$")
	for scanner.Scan() {
		message := scanner.Text()
		if re.MatchString(message) {
			count++
		}
	}
	return count
}

