		case "/":
			return m.Left.Expect(x * right)
		case "==":
			return m.Left.Expect(right)
		}
	}

	if !rOk {
		switch m.Op {
		case "+":
			return m.Right.Expect(x - left)
		case "-":
			return m.Right.Expect(left - x)
		case "*":
			return m.Right.Expect(x / left)
