		line := scanner.Text()
		if strings.Contains(line, "map:") {
			if len(currentRanges) > 0 {
				maps = append(maps, currentRanges)
				currentRanges = nil
			}
		} else if strings.HasPrefix(line, "seeds:") {
			seedStrs := strings.Split(line[7:], " ")
